import { useContext, useEffect } from "react";
import TrackerContext from "../context/TrackerContext";

const IncomeExpense = () => {
    const{setBalance,income, expense} = useContext(TrackerContext)
    
    useEffect(()=> {
        setBalance(Math.abs(sumUpIncome-sumUpExpense))
    }, [income,expense])

    const sumUpIncome= income.map((x)=> Number(x)).reduce((pre,current)=> pre+current, 0)
    const sumUpExpense = expense.map((x)=> Number(x)).reduce((pre,current)=> pre+current, 0)

    
    return ( 
        <div>
            <div className="income">
                <h2>income:{sumUpIncome}</h2>
            </div>
            <div className="expense">
                <h2>expense:{sumUpExpense}</h2>
            </div>
        </div>
     );
}
 
export default IncomeExpense;
