import { useContext, useRef} from "react";
import TrackerContext from "../context/TrackerContext";



const AddTransaction = () => {
    const {setIncome, setTransaction,transaction, setExpense, setAmount,setTitle, amount, title,income,expense} = useContext(TrackerContext)
    const ref1 = useRef(null)
    const ref2 = useRef(null)

    const amountHandler = (e)=>{
        setAmount(e.target.value)   
    }
    
    const titleHandler = (e)=>{
        setTitle(e.target.value)
    }

    const clickHandler = (e)=>{
        setTransaction([...transaction,{title:title,amount:amount }])
        setAmount("")
        setTitle("")
        checkHandler()
        e.preventDefault()
    }
    const checkHandler = ()=>{
        if(ref1.current.checked){
            setIncome([...income, amount])
        }
        else{
            setExpense([...expense, amount])
        }
            
    }

    return ( 
        <>
            <form action="">
            <label htmlFor="title"> Title:
                <input onChange={titleHandler}  value={title} className="title" type="text" />
            </label>
            <label htmlFor=""> Amount:
                <input onChange={amountHandler} value={amount}className="amount" type="number" />
            </label>
            <input ref={ref1} name="radio"  type="radio" defaultChecked/>Income
            <input ref={ref2} name="radio" type="radio"/>Expense
            <button onClick={clickHandler} >Add transaction</button>
        </form>

        </>
                

        
     );
}
 
export default AddTransaction;
