import { useState, useEffect } from "react";
import Main from "./Main";

const Home = () => {
  const [dataBlog, setData] = useState();
  const [name, setName] = useState("lewandoski");

  const handleDelete = (e) => {
    const newBlog = dataBlog.filter((blog) => blog.id !== e);
    // console.log(e);
    setData(newBlog);
  };

  useEffect(() => {
    fetch("https://v2.jokeapi.dev/joke/Any").then((res) =>
      res.json().then((data) => setData(data))
    );
  }, []);

  return (
    <div className="home">
      <p>HOME</p>

      <Main dataBlog={dataBlog} handleDelete={handleDelete} />
      <button onClick={() => setName("mario")}>click me</button>
    </div>
  );
};
export default Home;
