import { useState } from "react";
import Main from "./Main";

const Home = () => {
  const [dataBlog, setData] = useState([
    { title: "first section", author: "alex", id: 1 },
    { title: "second section", author: "alex", id: 1 },
    { title: "third section", author: "alex", id: 1 },
  ]);
  const handleDelete = (x) => {
    const newBlog = dataBlog.filter((blog) => blog.id !== x);
    setData(newBlog);
  };
  return (
    <div className="home">
      <p>HOME</p>

      <Main dataBlog={dataBlog} handleDelete={handleDelete} />
    </div>
  );
};
export default Home;
