import { useState, useEffect } from "react";
import Main from "./Main";

const Home = () => {
  const [dataBlog, setData] = useState(null);
  const [isPending, setisPending] = useState(true);
  const [error, setError] = useState(null);

  const handleDelete = (e) => {
    const newBlog = dataBlog.filter((blog) => blog.category !== e);
    // console.log(e);
    setData(newBlog);
  };

  useEffect(() => {
    fetch("https://v2.jokeapi.dev/joke/Any")
      .then((res) => {
        if (!res.ok) {
          throw Error("could not fetch the data");
        }
        console.log(res);
        return res.json();
      })
      .then((data) => {
        setData([data]);
        setisPending(false);
        console.log(data);
      })
      .catch((err) => {
        setError(err.message);
      });
  }, []);

  return (
    <div className="home">
      <p>HOME</p>
      {errror && <div>{error}</div>}
      {isPending && <div>Loading</div>}
      {dataBlog && <Main dataBlog={dataBlog} handleDelete={handleDelete} />}
      {/* <button onClick={() => setName("mario")}>click me</button> */}
    </div>
  );
};
export default Home;
