import { useState, useEffect } from "react";
import Main from "./Main";

const Home = () => {
  const [dataBlog, setData] = useState([
    { title: "first section", author: "alex", id: 1 },
    { title: "second section", author: "alex", id: 2 },
    { title: "third section", author: "alex", id: 3 },
  ]);
  const [name, setName] = useState("lewandoski");

  const handleDelete = (e) => {
    const newBlog = dataBlog.filter((blog) => blog.id !== e);
    // console.log(e);
    setData(newBlog);
  };

  useEffect(() => {
    console.log("calisti");
    console.log(name);
  }, [name]);

  return (
    <div className="home">
      <p>HOME</p>

      <Main dataBlog={dataBlog} handleDelete={handleDelete} />
      <button onClick={() => setName("mario")}>click me</button>
    </div>
  );
};
export default Home;
